get_ipython().run_cell_magic('html', '', '<input type="range" min="3" max="15" value="3" name="topics" oninput="graph(+this.value)">\n<label for="topics">Topics: </label><em id="topics" style="font-style: normal;">3</em>\n<div id="emojiN"></div>\n\n<script type="text/javascript">   \nfunction graph(n) {\n    document.getElementById(\'topics\').innerHTML = n\n    var width = 600\n    var height = 2000\n    var margin = 30\n    var fac = n => !(n > 1) ? 1 : fac(n - 1) * n;\n    var r = 3 \n    var soup = \'üêà,üê¶,üê≥,üêß,üêï,üêô,üêù,üêÑ,üê™,üêç,üêû,üê¨,üêë,üêâ,üê§,üê¢,üêí,üêò,üê†,üêÅ\'.split(\',\');\n    \n    var topics = d3.range(n).map(d=>soup[d])\n    var sets = []\n    for (let i = 0; i < topics.length - 1; i++) {\n        for (let j = i+1; j < topics.length - 1; j++) {\n            for (let k = j+1; k < topics.length; k++) {\n                var temp = []\n                temp.push(topics[i])\n                temp.push(topics[j])\n                temp.push(topics[k])\n                sets.push(temp)\n            }\n        }\n    }\n\n    var triangles = fac(n) / ( fac(r) * fac(n - r) )\n    var tri_col = 10\n    var tri_row = Math.ceil(sets.length/tri_col)\n    var tri_size = 20\n    var x = d3.scaleLinear().range([margin , width - margin]).domain([0,tri_col-1])\n    var y = d3.scaleBand().range([margin, height-margin]).domain(d3.range(tri_row))\n  \n    var tri_x = [(tri_size/2), tri_size, 0]\n    var tri_y = [0, tri_size, tri_size]   \n    var palette = d3.interpolateTurbo\n    var color = d3.scaleLinear().range([0,1]).domain([0,topics.length-1])\n    d3.select("div#emojiN").select("svg").remove()\n    var svg = d3.select("div#emojiN").append("svg")\n        .attr("width", width)\n        .attr("height", height)\n        \n    var g = svg.selectAll("g")\n        .data(sets)\n        .join("g")\n        .attr("transform", (d,i) => "translate("+(x(i%tri_col))+","+(y(Math.floor(i/tri_col))+(y.bandwidth()/2))+")")\n    g.append("line")\n        .attr("x1", (d,i)=> tri_x[0])\n        .attr("y1", (d,i)=> tri_y[0])\n        .attr("x2", (d,i)=> tri_x[1])\n        .attr("y2", (d,i)=> tri_y[1])\n        .style("stroke", "black")\n        .style("stroke-width", "2px")\n    g.append("line")\n        .attr("x1", (d,i)=> tri_x[1])\n        .attr("y1", (d,i)=> tri_y[1])\n        .attr("x2", (d,i)=> tri_x[2])\n        .attr("y2", (d,i)=> tri_y[2])\n        .style("stroke", "black")\n        .style("stroke-width", "2px")\n    g.append("line")\n        .attr("x1", (d,i)=> tri_x[2])\n        .attr("y1", (d,i)=> tri_y[2])\n        .attr("x2", (d,i)=> tri_x[0])\n        .attr("y2", (d,i)=> tri_y[0])\n        .style("stroke", "black")\n        .style("stroke-width", "2px")    \n    \n    g.selectAll("text")\n        .data(d=>d)\n        .join("text")\n        .attr("x", (d,i)=> tri_x[i])\n        .attr("y", (d,i)=> tri_y[i])\n        .style("text-anchor", "middle")\n        .style("alignment-baseline","middle")\n        .text(d=>d)\n\n}\ngraph(3)\n</script>')

